import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { ChevronDown, ChevronRight } from 'lucide-react';
import './Dashboard.css';

export default function Dashboard() {
  const [godowns, setGodowns] = useState([]);
  const [selectedGodown, setSelectedGodown] = useState(null);
  const [items, setItems] = useState([]);
  const [expandedGodowns, setExpandedGodowns] = useState({});
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5005/api/godowns')
      .then(response => setGodowns(response.data))
      .catch(error => console.error('Error fetching godowns:', error));
  }, []);

  const handleGodownClick = (godownId) => {
    setSelectedGodown(godownId);
    axios.get(`http://localhost:5005/api/items?godown_id=${godownId}`)
      .then(response => setItems(response.data))
      .catch(error => console.error('Error fetching items:', error));
  };

  const toggleGodown = (godownId) => {
    setExpandedGodowns(prevState => ({
      ...prevState,
      [godownId]: !prevState[godownId]
    }));
  };

const renderTree = (parentId) => {
  const children = godowns.filter(godown => godown.parent_godown === parentId);
  if (children.length === 0) return null;

  return (
    <ul className="godown-tree">
      {children.map(child => {
        const hasChildren = godowns.some(godown => godown.parent_godown === child.id); // Check if child has children
        return (
          <li key={child.id} className="godown-tree-item">
            <div className="godown-node" onClick={() => toggleGodown(child.id)}>
              {expandedGodowns[child.id] ? <ChevronDown size={18} /> : <ChevronRight size={18} />}
              <span style={{ marginLeft: '5px' }}>{child.name}</span>
            </div>
            {expandedGodowns[child.id] && (
              <>
                {/* Render child nodes */}
                {renderTree(child.id)}
                {/* Show "View Items" button only for leaf nodes */}
                {!hasChildren && (
                  <button className="view-items-button" onClick={() => handleGodownClick(child.id)}>
                    View Items
                  </button>
                )}
              </>
            )}
          </li>
        );
      })}
    </ul>
  );
};



  return (
    <div className="dashboard">
      <button className="menu-button" onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
        â˜°
      </button>
      <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <h2 className="title">Godowns</h2>
        {renderTree(null)}
      </div>
      <div className="main">
        <h2 className="title">Item Details</h2>
        {items.length === 0 ? (
          <p>No items found for this location</p>
        ) : (
          <div className="items-list">
            {items.map(item => (
              <div className="item-card" key={item.item_id}>
                <img className="item-image" src={item.image_url} alt={item.name} />
                <div className="item-details">
                  <h3 className="item-name">{item.name}</h3>
                  <p className="item-info">Category: {item.category}</p>
                  <p className="item-info">Price: ${item.price}</p>
                  <p className="item-info">Brand: {item.brand}</p>
                  <p className="item-info">Quantity: {item.quantity}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
