import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  DashboardContainer, Sidebar, Main, Title, GodownTree, GodownNode,
  GodownContent, ViewItemsButton, ItemsList, ItemCard, ItemImageContainer,
  ItemImage, ItemDetails, ItemName, ItemInfo, MenuButton
} from './DashboardStyles';
import { ChevronDown, ChevronRight } from 'lucide-react';
import { useDrag, useDrop } from 'react-dnd';

export default function Dashboard() {
  const [godowns, setGodowns] = useState([]);
  const [selectedGodown, setSelectedGodown] = useState(null);
  const [items, setItems] = useState([]);
  const [expandedGodowns, setExpandedGodowns] = useState({});
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5005/api/godowns')
      .then(response => setGodowns(response.data))
      .catch(error => console.error('Error fetching godowns:', error));
  }, []);

  const handleGodownClick = (godownId) => {
    setSelectedGodown(godownId);
    axios.get(`http://localhost:5005/api/items?godown_id=${godownId}`)
      .then(response => setItems(response.data))
      .catch(error => console.error('Error fetching items:', error));
  };

  const toggleGodown = (godownId) => {
    setExpandedGodowns(prevState => ({
      ...prevState,
      [godownId]: !prevState[godownId]
    }));
  };

  const handleDrop = (draggedItemId, newGodownId) => {
    // Update item location in state
    setItems(prevItems =>
      prevItems.map(item =>
        item.item_id === draggedItemId ? { ...item, godown_id: newGodownId } : item
      )
    );

    // Make API call to update item location on server
    axios.put(`http://localhost:5005/api/items/${draggedItemId}`, { godown_id: newGodownId })
      .then(response => console.log(response.data))
      .catch(error => console.error('Error moving item:', error));
  };

  const filteredGodowns = godowns.filter(godown =>
    godown.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const filteredItems = items.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (selectedCategory === '' || item.category === selectedCategory)
  );

  const renderTree = (parentId) => {
    const children = filteredGodowns.filter(godown => godown.parent_godown === parentId);
    if (children.length === 0) return null;

    return (
      <GodownTree>
        {children.map(child => (
          <GodownNode key={child.id}>
            <GodownContent>
              <span onClick={() => toggleGodown(child.id)}>
                {expandedGodowns[child.id] ? <ChevronDown size={18} /> : <ChevronRight size={18} />}
                <span style={{ marginLeft: '5px' }}>{child.name}</span>
              </span>
              <ViewItemsButton onClick={() => handleGodownClick(child.id)}>
                View Items
              </ViewItemsButton>
            </GodownContent>
            {expandedGodowns[child.id] && renderTree(child.id)}
          </GodownNode>
        ))}
      </GodownTree>
    );
  };

  return (
    <DashboardContainer>
      <MenuButton onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
        â˜°
      </MenuButton>
      <Sidebar isOpen={isSidebarOpen}>
        <Title>Godowns</Title>
        <input 
          type="text" 
          placeholder="Search Godowns..." 
          value={searchTerm} 
          onChange={(e) => setSearchTerm(e.target.value)} 
        />
        {renderTree(null)}
      </Sidebar>
      <Main>
        <Title>Item Details</Title>
        <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="">All Categories</option>
          <option value="Toys">Toys</option>
          <option value="Electronics">Electronics</option>
          {/* Add other categories */}
        </select>
        {filteredItems.length === 0 ? (
          <p>No items found for this location</p>
        ) : (
          <ItemsList>
            {filteredItems.map(item => {
              const [{ isDragging }, drag] = useDrag(() => ({
                type: 'ITEM',
                item: { id: item.item_id },
                collect: (monitor) => ({
                  isDragging: !!monitor.isDragging(),
                }),
              }));

              return (
                <ItemCard ref={drag} key={item.item_id} style={{ opacity: isDragging ? 0.5 : 1 }}>
                  <ItemImageContainer>
                    <ItemImage src={item.image_url} alt={item.name} />
                  </ItemImageContainer>
                  <ItemDetails>
                    <ItemName>{item.name}</ItemName>
                    <ItemInfo>Category: {item.category}</ItemInfo>
                    <ItemInfo>Price: ${item.price}</ItemInfo>
                    <ItemInfo>Brand: {item.brand}</ItemInfo>
                    <ItemInfo>Quantity: {item.quantity}</ItemInfo>
                  </ItemDetails>
                </ItemCard>
              );
            })}
          </ItemsList>
        )}
      </Main>
    </DashboardContainer>
  );
}
