import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  DashboardContainer, Sidebar, Main, Title, GodownTree, GodownNode,
  GodownContent, ViewItemsButton, ItemsList, ItemCard, ItemImageContainer,
  ItemImage, ItemDetails, ItemName, ItemInfo, MenuButton, SearchBar, LoadingSpinner
} from './DashboardStyles'; // Add the LoadingSpinner style to your DashboardStyles
import { ChevronDown, ChevronRight, Package } from 'lucide-react';

export default function Dashboard() {
  const [godowns, setGodowns] = useState([]);
  const [selectedGodown, setSelectedGodown] = useState(null);
  const [items, setItems] = useState([]);
  const [expandedGodowns, setExpandedGodowns] = useState({});
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [loadingGodowns, setLoadingGodowns] = useState(true); // Loading state for godowns
  const [loadingItems, setLoadingItems] = useState(false); // Loading state for items

  useEffect(() => {
    axios.get('http://localhost:5006/api/godowns')
      .then(response => {
        setGodowns(response.data);
        setLoadingGodowns(false); // Stop loading when data is fetched
      })
      .catch(error => {
        console.error('Error fetching godowns:', error);
        setLoadingGodowns(false); // Stop loading on error
      });
  }, []);

  const handleGodownClick = (godownId) => {
    setSelectedGodown(godownId);
    setLoadingItems(true); // Start loading items
    axios.get(`http://localhost:5006/api/items?godown_id=${godownId}`)
      .then(response => {
        setItems(response.data);
        setLoadingItems(false); // Stop loading when data is fetched
      })
      .catch(error => {
        console.error('Error fetching items:', error);
        setLoadingItems(false); // Stop loading on error
      });

    if (window.innerWidth <= 768) {
      setIsSidebarOpen(false);
    }
  };

  const toggleGodown = (godownId) => {
    setExpandedGodowns(prevState => ({
      ...prevState,
      [godownId]: !prevState[godownId]
    }));
  };

  const filteredItems = items.filter(item => 
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

 const renderTree = (parentId) => {
  const children = godowns.filter(godown => godown.parent_godown === parentId);
  if (children.length === 0) return null;

  // Check if the last batch of children have no further children
  const allLastBatchChildrenHaveNoChildren = children.every(child => {
    return !godowns.some(godown => godown.parent_godown === child.id);
  });

  return (
    <GodownTree>
      {children.map((child, index) => {
        const hasChildren = godowns.some(godown => godown.parent_godown === child.id);

        return (
          <GodownNode key={child.id}>
            <GodownContent
              onClick={() => hasChildren ? toggleGodown(child.id) : handleGodownClick(child.id)}
            >
              <span>
                {(!allLastBatchChildrenHaveNoChildren || hasChildren) ? (
                  expandedGodowns[child.id] ? <ChevronDown size={18} /> : <ChevronRight size={18} />
                ) : null}
                <span style={{ marginLeft: '5px' }}>{child.name}</span>
              </span>
              {!hasChildren && (
                <ViewItemsButton 
                  onClick={() => handleGodownClick(child.id)}
                  isSelected={selectedGodown === child.id}
                >
                  <Package size={14} style={{ marginRight: '5px' }} />
                  View Items
                </ViewItemsButton>
              )}
            </GodownContent>
            {expandedGodowns[child.id] && renderTree(child.id)}
          </GodownNode>
        );
      })}
    </GodownTree>
  );
};



  return (
    <DashboardContainer>
      <MenuButton onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
        â˜°
      </MenuButton>
      <Sidebar $isOpen={isSidebarOpen}>
        <Title>Godowns</Title>
        {loadingGodowns ? (
          <LoadingSpinner>Loading...</LoadingSpinner> // Show loading spinner for godowns
        ) : (
          renderTree(null)
        )}
      </Sidebar>
      <Main>
        <Title>Item Details</Title>
        <SearchBar
          type="text"
          placeholder="Search items..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        {loadingItems ? (
          <LoadingSpinner>Loading...</LoadingSpinner> // Show loading spinner for items
        ) : filteredItems.length === 0 ? (
          <p>No items found for this location</p>
        ) : (
          <ItemsList>
            {filteredItems.map(item => (
              <ItemCard key={item.item_id}>
                <ItemImageContainer>
                  <ItemImage src={item.image_url} alt={item.name} />
                </ItemImageContainer>
                <ItemDetails>
                  <ItemName>{item.name}</ItemName>
                  <ItemInfo>Category: {item.category}</ItemInfo>
                  <ItemInfo>Price: ${item.price}</ItemInfo>
                  <ItemInfo>Brand: {item.brand}</ItemInfo>
                  <ItemInfo>Quantity: {item.quantity}</ItemInfo>
                </ItemDetails>
              </ItemCard>
            ))}
          </ItemsList>
        )}
      </Main>
    </DashboardContainer>
  );
}
