import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { ChevronDown, ChevronRight } from 'lucide-react';

const DashboardContainer = styled.div`
  display: flex;
  height: 100vh;
  overflow: hidden;
`;

const Sidebar = styled.div<{ isOpen: boolean }>`
  width: ${props => props.isOpen ? '300px' : '0'};
  background-color: #ffffff;
  padding: 20px;
  overflow-y: auto;
  transition: width 0.3s ease;

  @media (max-width: 768px) {
    position: fixed;
    top: 0;
    left: 0;
    height: 100vh;
    z-index: 1000;
  }
`;

const Main = styled.div`
  flex: 1;
  padding: 20px;
  overflow-y: auto;
`;

const Title = styled.h2`
  color: #e94560;
  margin-bottom: 30px;
  font-size: 28px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 1px;
  position: relative;
  padding-bottom: 10px;

  &::after {
    content: '';
    position: absolute;
    left: 0;
    bottom: 0;
    width: 50px;
    height: 3px;
    background-color: #e94560;
    transition: width 0.3s ease;
  }

  &:hover::after {
    width: 100px;
  }
`;

const GodownTree = styled.ul`
  list-style-type: none;
  padding-left: 20px;
  position: relative;
`;

const GodownNode = styled.li`
  position: relative;
  margin-bottom: 10px;

  &::before {
    content: '';
    position: absolute;
    left: -10px;
    top: 20px;
    bottom: 0;
    width: 2px;
    background-color: #e94560;
    z-index: -1;
  }

  &:last-child::before {
    display: none;
  }
`;

const GodownContent = styled.div`
  display: flex;
  align-items: center;
  padding: 10px;
  cursor: pointer;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #457fc6;
  }
`;

const ViewItemsButton = styled.button`
  background-color: #e94560;
  color: white;
  border: none;
  padding: 5px 10px;
  margin-left: 10px;
  cursor: pointer;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #ff6b6b;
  }
`;

const ItemsList = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 20px;
`;

const ItemCard = styled.div`
  background-color: #ffffff;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  width: 300px;

  &:hover {
    transform: translateY(-10px) scale(1.02);
    box-shadow: 0 12px 20px rgba(0, 0, 0, 0.2);
  }
`;

const ItemImageContainer = styled.div`
  width: 300px;
  height: 200px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #f8f8f8;
  overflow: hidden;
`;

const ItemImage = styled.img`
  max-width: 100%;
  max-height: 100%;
  width: auto;
  height: auto;
  object-fit: contain;
  transition: transform 0.3s ease;

  ${ItemCard}:hover & {
    transform: scale(1.05);
  }
`;

const ItemDetails = styled.div`
  padding: 15px;
`;

const ItemName = styled.h3`
  margin: 0 0 10px;
  color: #e94560;
  font-size: 18px;
  font-weight: 600;
`;

const ItemInfo = styled.p`
  margin: 5px 0;
  font-size: 14px;
  color: #333;
`;

const MenuButton = styled.button`
  display: none;
  background: none;
  border: none;
  color: #e94560;
  font-size: 24px;
  cursor: pointer;

  @media (max-width: 768px) {
    display: block;
    position: fixed;
    top: 10px;
    left: 10px;
    z-index: 1001;
  }
`;

export default function Dashboard() {
  const [godowns, setGodowns] = useState([]);
  const [selectedGodown, setSelectedGodown] = useState(null);
  const [items, setItems] = useState([]);
  const [expandedGodowns, setExpandedGodowns] = useState({});
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5005/api/godowns')
      .then(response => setGodowns(response.data))
      .catch(error => console.error('Error fetching godowns:', error));
  }, []);

  const handleGodownClick = (godownId) => {
    setSelectedGodown(godownId);
    axios.get(`http://localhost:5005/api/items?godown_id=${godownId}`)
      .then(response => setItems(response.data))
      .catch(error => console.error('Error fetching items:', error));
  };

  const toggleGodown = (godownId) => {
    setExpandedGodowns(prevState => ({
      ...prevState,
      [godownId]: !prevState[godownId]
    }));
  };

  const renderTree = (parentId) => {
    const children = godowns.filter(godown => godown.parent_godown === parentId);
    if (children.length === 0) return null;

    return (
      <GodownTree>
        {children.map(child => (
          <GodownNode key={child.id}>
            <GodownContent>
              <span onClick={() => toggleGodown(child.id)}>
                {expandedGodowns[child.id] ? <ChevronDown size={18} /> : <ChevronRight size={18} />}
                <span style={{ marginLeft: '5px' }}>{child.name}</span>
              </span>
              <ViewItemsButton onClick={() => handleGodownClick(child.id)}>
                View Items
              </ViewItemsButton>
            </GodownContent>
            {expandedGodowns[child.id] && renderTree(child.id)}
          </GodownNode>
        ))}
      </GodownTree>
    );
  };

  return (
    <DashboardContainer>
      <MenuButton onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
        â˜°
      </MenuButton>
      <Sidebar isOpen={isSidebarOpen}>
        <Title>Godowns</Title>
        {renderTree(null)}
      </Sidebar>
      <Main>
        <Title>Item Details</Title>
        {items.length === 0 ? (
          <p>No items found for this location</p>
        ) : (
          <ItemsList>
            {items.map(item => (
              <ItemCard key={item.item_id}>
                <ItemImageContainer>
                  <ItemImage src={item.image_url} alt={item.name} />
                </ItemImageContainer>
                <ItemDetails>
                  <ItemName>{item.name}</ItemName>
                  <ItemInfo>Category: {item.category}</ItemInfo>
                  <ItemInfo>Price: ${item.price}</ItemInfo>
                  <ItemInfo>Brand: {item.brand}</ItemInfo>
                  <ItemInfo>Quantity: {item.quantity}</ItemInfo>
                </ItemDetails>
              </ItemCard>
            ))}
          </ItemsList>
        )}
      </Main>
    </DashboardContainer>
  );
}
